import React from 'react';
import { connect } from 'react-redux';
import { getCards } from './actions/getcards';
import './cssfiles.js'
import Heading from './fields.js'
import Shelf from './img/shelf.jpg';
import Book from './img/book.jpg';
import AddBook from './addBookForm'
import SendImage from './actions/sendImage'
import FormSubmit from './actions/formSubmit'



const readFile = (event) => {
  SendImage(event.target.files[0]).then(file => {
        this.setState({bookfile: file[0].url});
  })
  event.preventDefault();
}




const App = ({  cards, onFindCard , onhandleSubmit, onhandleTitle,  onhandleAuthor, onGetCards, ownProps }) => {
  let searchInput = '';
  let mobilesearchInput = '';
  let title = '';
  let author = '';
  let bookfile = '';
  const mobileOpen = false;
  const handleSubmit = (event) => {
      event.preventDefault();
      let data = {
        title : title,
        author : author,
        bookfile : bookfile,
      }
      onhandleSubmit(JSON.stringify(data))
    }
    const updateTitle = (event) => {
      onhandleTitle( title.value)
    }
    const updateAuthor = (event) => {
      onhandleAuthor( author.value)
    }
  const findCard = () => { onFindCard(searchInput.value) }
  let shelf_Style = (a) => {return { backgroundSize: "cover",display: "block",minHeight: "100vh",overflowX: "hidden",height: "100%", backgroundImage: "url(" +Shelf+ ")" }}
  let book_Style = (a) => {return { backgroundSize: "cover",display: "flex",minHeight: "350px",overflowX: "hidden",height: "100%", backgroundImage: "url(" +Book+ ")" }}



  let text_Style = () => {return { color: "#fff" }}
      return (
      <div className="Cards" style={shelf_Style(Shelf)}>
          <Heading />
        <div className="fields" >
          <div className="row blueb">
              <div className="container">
                <div className="col-lg-12 col-xs-12 flex cardsearch">
                  <input type="text" onChange={findCard} ref={(input) => { searchInput = input}} />
                </div>
              </div>
          </div>
        </div>
        <div className="container">
        <div className="row">
          {cards.map((card, index) =>
            <div key={index} className="col-md-3 col-sm-6">
              <div>
              <div className="el_card" style={book_Style(Book)}>
                        <h4 className="title" style={text_Style()}>{card.title}</h4>
                        <div className="author" style={text_Style()}>{card.author}</div>
              </div>
            </div>
            </div>
        )}
        </div>
        <form className="books_add" onSubmit={handleSubmit}>
            <div className="form_group">
              <label> Title</label>
              <input name="title" type="text" value={title}  ref={(input) => { title = input}} onChange={updateTitle}/>
            </div>
            <div className="form_group">
              <label> author</label>
              <input name="author" type="text" value={author}  ref={(input) => { author = input}} onChange={updateAuthor}/>
            </div>
            <div className="form_group">
              <label> bookfile</label>
              <input id="fileupload" name="bookfile" type="file" accept="image/*" onChange={(event)=> { readFile(event) }} onClick={(event)=> { event.target.value = null }} />
            </div>
            <div className="form_group">
              <label> submit</label>
              <input type="submit" />
            </div>
        </form>
        </div>
        <div className="flex center">
         <button id="refresh" onClick={onGetCards}>Refresh</button>
         </div>
      </div>
    )
}
export default connect(
  (state, ownProps) => ({
    cards: state.cards .filter( card => card.title.toLowerCase().includes(state.filterCards.toLowerCase()) ),
  ownProps
  }),
  dispatch => ({
    onFindCard : (payload) => {
      dispatch({ type: 'FIND_CARD', payload: payload})
    },
    onhandleSubmit : (data) => {
      dispatch({ type: 'ADD_CARD', payload: data})
    },
    onhandleTitle: (data) => {
        dispatch({ type: 'UPDATE_TITLE', payload: data})
    },
    onhandleAuthor: (data) => {
        dispatch({ type: 'UPDATE_AUTHOR', payload: data})
    },
    onGetCards: () => {
        dispatch(getCards())
    }
    }),
)(App);
