import React, { Component } from 'react';



class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {title: '',author: '',title: '',};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (


      <form class="books_add" onSubmit={this.handleSubmit}>
          <div class="form_group">
            <label> Title</label>
            <input name="title" type="text" value={this.state.title}/>
          </div>
          <div class="form_group">
            <label> author</label>
            <input name="author" type="text" value={this.state.author}/>
          </div>

          <div class="form_group">
            <label> bookfile</label>
            <input name="bookfile" type="file" value={this.state.bookfile} onChange={this.handleChange} />
          </div>
          <div class="form_group">
            <label> submit</label>
            <input type="submit" />
          </div>


      </form>



    );
  }
}



export default NameForm;
