import React, { Component } from 'react';




class SingleDropdown extends Component {
  constructor(props){
    this.props = props
  }
  render() {
    return (
      <div className="dd-wrapper">
        <div className="dd-header" onClick={(ev) => this.props.toggleList(ev, this.props.filterTypes.districts.options[0].key)}>
          <div className="dd-header-title">{this.props.filterTypes.districts.headerTitle}</div>
          {this.props.filterTypes.districts.listOpen
            ? <FontAwesome name="angle-up"/>
            : <FontAwesome name="angle-down"/>
          }
          <span className="gray_icon"><img alt="search" src={PlaceIcon} /></span>
        </div>
        {this.props.filterTypes.districts.listOpen && <ul className="dd-list">
           {this.props.filterTypes.districts.options.map((item) => (
             <li className="dd-list-item" key={item.title} onClick={() => selectedDistrict(item.id, item.key, item.value)}>
               {item.title} {item.selected && <FontAwesome name="check"/>}
             </li>
            ))}
        </ul>}
      </div>
    )
  }
}

export default SingleDropdown;
